CREATE TABLE admin (id SERIAL NOT NULL, email VARCHAR(180) NOT NULL, roles JSON NOT NULL, password VARCHAR(255) NOT NULL, PRIMARY KEY(id));
CREATE UNIQUE INDEX UNIQ_880E0D76E7927C74 ON admin (email);
CREATE TABLE chambre (id SERIAL NOT NULL, hotel_id INT DEFAULT NULL, classement_id INT NOT NULL, number INT NOT NULL, floor INT NOT NULL, area DOUBLE PRECISION NOT NULL, price_per_night DOUBLE PRECISION NOT NULL, is_available BOOLEAN NOT NULL, PRIMARY KEY(id));
CREATE INDEX IDX_C509E4FF3243BB18 ON chambre (hotel_id);
CREATE INDEX IDX_C509E4FFA513A63E ON chambre (classement_id);
CREATE TABLE chambre_service (chambre_id INT NOT NULL, service_id INT NOT NULL, PRIMARY KEY(chambre_id, service_id));
CREATE INDEX IDX_428BC92E9B177F54 ON chambre_service (chambre_id);
CREATE INDEX IDX_428BC92EED5CA9E6 ON chambre_service (service_id);
CREATE TABLE classement_h (id SERIAL NOT NULL, name VARCHAR(100) NOT NULL, description TEXT DEFAULT NULL, base_price DOUBLE PRECISION NOT NULL, PRIMARY KEY(id));
CREATE TABLE client (id SERIAL NOT NULL, email VARCHAR(180) NOT NULL, roles JSON NOT NULL, password VARCHAR(255) NOT NULL, first_name VARCHAR(100) DEFAULT NULL, last_name VARCHAR(100) DEFAULT NULL, created_at TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, PRIMARY KEY(id));
CREATE UNIQUE INDEX UNIQ_C7440455E7927C74 ON client (email);
COMMENT ON COLUMN client.created_at IS '(DC2Type:datetime_immutable)';
CREATE TABLE hotel (id SERIAL NOT NULL, name VARCHAR(255) NOT NULL, adress VARCHAR(255) NOT NULL, city VARCHAR(100) NOT NULL, description TEXT DEFAULT NULL, created_at TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, PRIMARY KEY(id));
COMMENT ON COLUMN hotel.created_at IS '(DC2Type:datetime_immutable)';
CREATE TABLE reservation (id SERIAL NOT NULL, client_id INT NOT NULL, chambre_id INT NOT NULL, check_in DATE NOT NULL, check_out DATE NOT NULL, status VARCHAR(50) NOT NULL, total_price DOUBLE PRECISION NOT NULL, created_at TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, PRIMARY KEY(id));
CREATE INDEX IDX_42C8495519EB6921 ON reservation (client_id);
CREATE INDEX IDX_42C849559B177F54 ON reservation (chambre_id);
COMMENT ON COLUMN reservation.check_in IS '(DC2Type:date_immutable)';
COMMENT ON COLUMN reservation.check_out IS '(DC2Type:date_immutable)';
COMMENT ON COLUMN reservation.created_at IS '(DC2Type:datetime_immutable)';
CREATE TABLE service (id SERIAL NOT NULL, name VARCHAR(100) NOT NULL, description TEXT DEFAULT NULL, PRIMARY KEY(id));
CREATE TABLE travaux (id SERIAL NOT NULL, chambre_id INT NOT NULL, title VARCHAR(250) NOT NULL, description TEXT DEFAULT NULL, start_date TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, end_date TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, is_done BOOLEAN NOT NULL, PRIMARY KEY(id));
CREATE INDEX IDX_6C24F39B9B177F54 ON travaux (chambre_id);
COMMENT ON COLUMN travaux.start_date IS '(DC2Type:datetime_immutable)';
COMMENT ON COLUMN travaux.end_date IS '(DC2Type:datetime_immutable)';
CREATE TABLE messenger_messages (id BIGSERIAL NOT NULL, body TEXT NOT NULL, headers TEXT NOT NULL, queue_name VARCHAR(190) NOT NULL, created_at TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, available_at TIMESTAMP(0) WITHOUT TIME ZONE NOT NULL, delivered_at TIMESTAMP(0) WITHOUT TIME ZONE DEFAULT NULL, PRIMARY KEY(id));
CREATE INDEX IDX_75EA56E0FB7336F0 ON messenger_messages (queue_name);
CREATE INDEX IDX_75EA56E0E3BD61CE ON messenger_messages (available_at);
CREATE INDEX IDX_75EA56E016BA31DB ON messenger_messages (delivered_at);
COMMENT ON COLUMN messenger_messages.created_at IS '(DC2Type:datetime_immutable)';
COMMENT ON COLUMN messenger_messages.available_at IS '(DC2Type:datetime_immutable)';
COMMENT ON COLUMN messenger_messages.delivered_at IS '(DC2Type:datetime_immutable)';
CREATE OR REPLACE FUNCTION notify_messenger_messages() RETURNS TRIGGER AS $$
    BEGIN
        PERFORM pg_notify('messenger_messages', NEW.queue_name::text);
        RETURN NEW;
    END;
$$ LANGUAGE plpgsql;;
DROP TRIGGER IF EXISTS notify_trigger ON messenger_messages;;
CREATE TRIGGER notify_trigger AFTER INSERT OR UPDATE ON messenger_messages FOR EACH ROW EXECUTE PROCEDURE notify_messenger_messages();;
ALTER TABLE chambre ADD CONSTRAINT FK_C509E4FF3243BB18 FOREIGN KEY (hotel_id) REFERENCES hotel (id) NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE chambre ADD CONSTRAINT FK_C509E4FFA513A63E FOREIGN KEY (classement_id) REFERENCES classement_h (id) NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE chambre_service ADD CONSTRAINT FK_428BC92E9B177F54 FOREIGN KEY (chambre_id) REFERENCES chambre (id) ON DELETE CASCADE NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE chambre_service ADD CONSTRAINT FK_428BC92EED5CA9E6 FOREIGN KEY (service_id) REFERENCES service (id) ON DELETE CASCADE NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE reservation ADD CONSTRAINT FK_42C8495519EB6921 FOREIGN KEY (client_id) REFERENCES client (id) NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE reservation ADD CONSTRAINT FK_42C849559B177F54 FOREIGN KEY (chambre_id) REFERENCES chambre (id) NOT DEFERRABLE INITIALLY IMMEDIATE;
ALTER TABLE travaux ADD CONSTRAINT FK_6C24F39B9B177F54 FOREIGN KEY (chambre_id) REFERENCES chambre (id) NOT DEFERRABLE INITIALLY IMMEDIATE;
